window.onload=function(){var e=new Vue({el:"#main",data:{totalNumOfGamesStartingOffset:14e3,totalNumOfGames:0,randomCategories:[],categoriesToCheck:[],viewableCategories:[]},methods:{getTotalNumOfGames:function(){new Promise((e,t)=>{const o=new XMLHttpRequest;var a="https://www.speedrun.com/api/v1/games?_bulk=yes&max=1000&offset="+this.totalNumOfGamesStartingOffset;o.open("GET",a),o.onload=(()=>e(JSON.parse(o.responseText).data)),o.onerror=(()=>t(o.statusText)),o.send()}).then(t=>{t.length<1e3?(e.totalNumOfGames=e.totalNumOfGamesStartingOffset+t.length,e.getRandomGroupOfGames()):(e.totalNumOfGamesStartingOffset+=1e3,e.getTotalNumOfGames())})},getRandomGroupOfGames:function(){new Promise((e,t)=>{const o=new XMLHttpRequest;var a="https://www.speedrun.com/api/v1/games?offset="+this.getRandomNumber(this.totalNumOfGames)+"&embed=categories.game";o.open("GET",a),o.onload=(()=>e(JSON.parse(o.responseText).data)),o.onerror=(()=>t(o.statusText)),o.send()}).then(t=>{e.extractCategoriesFromGroupOfGames(t)})},extractCategoriesFromGroupOfGames:function(t){var o=[].concat.apply([],t.map(e=>e.categories.data));e.removePerLevelCategories(o)},removePerLevelCategories:function(t){var o=t.filter(e=>"per-game"===e.type);e.getRandomSetOfCategories(o)},getRandomNumber:function(e){return Math.floor(Math.random()*(e+1))},getRandomSetOfCategories:function(e,t=10){for(var o=[],a=[];o.length<t;){var r=this.getRandomNumber(e.length-1);-1===o.indexOf(r)&&o.push(r)}for(var n=0;n<o.length;n++)a.push(e[o[n]]);console.log(a)},getRecordFromCategoryObj:function(e){return new Promise((t,o)=>{const a=new XMLHttpRequest;var r="https://www.speedrun.com/api/v1/categories/"+e.id+"/records?top=1";a.open("GET",r),a.onload=(()=>t(JSON.parse(a.responseText).data)),a.onerror=(()=>o(a.statusText)),a.send()})},getRecordsFromCategoryArray:function(e){for(var t=0;t<e.length;t++);},isRecordSuitableForViewing:function(e){}},created:function(){this.getTotalNumOfGames()}})};